import { useState, useEffect } from "react";
import axios from "axios";
import Card from "./components/Card/Card";
import "./App.css";

function App() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState([]);
  const [originalData, setOriginalData] = useState([]);

  const changeHandler = e => {
    setInput(input => e.target.value.toLowerCase());
  }

  useEffect(() => {
    var patt = new RegExp(`${input}`);
    let value = originalData.filter(word => patt.test(word.name.toLowerCase()));
    setData(data=>value);
    console.log(input);
  },[input]);

  useEffect(() => {
    axios.get("https://jsonplaceholder.typicode.com/users").then((response) => {
      setData((data) => response.data);
      setOriginalData((originalData) => response.data);
    });
  },[]);

  return (
    <div className="searchContainer">
      <div className="alignCenter">


      <input type="text" placeholder="Search the list" autocomplete="off" name="name" onChange={changeHandler} />
      </div>
      <div className="dataContainer">
      {data.length &&
        data.map((element) => <Card id={element.id} name={element.name} />)}
      </div>
    </div>
  );
}

export default App;
